struct TreeNode {
    int val;
    TreeNode *left;
    TreeNode *right;
    TreeNode(int x) : val(x), left(NULL), right(NULL) {}
    TreeNode(int x, TreeNode* l, TreeNode* r) : val(x), left(l), right(r) {}
};

class Solution {
  public:
    bool canPermutePalindrome(const string &str) 
    {
	vector<int> table(256, 0);
	int count = 0;
	for (char c : str) {
	    count += ++table[c] % 2 == 0 ? -1 : 1;
	}
	return count < 2;
    }
};

